import { Component } from '@angular/core';
import { Otel } from 'src/app/models/Otel';
import { AuthService } from 'src/app/services/Auth.service';
import { OtelService } from 'src/app/services/Otel.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.sass'],
  providers: [OtelService],
})
export class DashboardComponent {
  newOtel = new Otel();
  iller = this.otelService.iller;
  constructor(
    private authService: AuthService,
    private otelService: OtelService
  ) {
    authService.kullanici.subscribe((kullanici) => {
      this.newOtel = kullanici.otel!;
    });
  }

  updateOtel() {
    this.otelService.updateOtel(this.newOtel);
  }
}
